{
    "operations": [
        {
            "path": "globalACI",
            "op": "add",
            "value": "(targetcontrol=\"1.2.840.113556.1.4.319\")(version 3.0; acl \"Allow paging for pingfederate\"; allow (read) userdn=\"ldap:///cn=pingfederate,cn=Root DNs,cn=config\";)"
        },
        {
            "path": "globalACI",
            "op": "add",
            "value": "(targetcontrol=\"1.2.840.113556.1.4.473\")(version 3.0; acl \"Allow Server Sort for pingfederate\"; allow (read) userdn=\"ldap:///cn=pingfederate,cn=Root DNs,cn=config\";)"
        },
        {
            "path": "globalACI",
            "op": "add",
            "value": "(targetcontrol=\"1.3.6.1.1.13.2\")(version 3.0; acl \"Anonymous access to the post-read request control as described in RFC 4527\"; allow (read) userdn=\"ldap:///anyone\";)"
        }
    ]
}

{
    "operations": [
        {
            "path": "globalACI",
            "op": "add",
            "value": "(target=\"ldap:///cn=alerts\")(targetattr=\"*||+\")(version 3.0; acl \"pingdatagovernance access to the alerts backend\"; allow(read,search,compare) userdn=\"ldap:///cn=pingdatagovernance,cn=Root DNs,cn=config\";)"
        },
        {
            "path": "globalACI",
            "op": "add",
            "value": "(target=\"ldap:///cn=monitor\")(targetattr=\"*||+\")(version 3.0; acl \"pingdatagovernance access to the monitor backend\"; allow(read,search,compare) userdn=\"ldap:///cn=Govepingdatagovernance,cn=Root DNs,cn=config\";)"
        },
        {
            "path": "globalACI",
            "op": "add",
            "value": "(targetcontrol=\"1.2.840.113556.1.4.1413||1.3.6.1.1.13.2||1.3.6.1.4.1.30221.2.5.2||1.3.6.1.4.1.30221.2.5.40||1.3.6.1.4.1.30221.2.5.44||1.3.6.1.1.12||2.16.840.1.113730.3.4.3\")(version 3.0; acl \"pingdatagovernance access to selected controls\"; allow (read) userdn=\"ldap:///cn=pingdatagovernance,cn=Root DNs,cn=config\";)"
        },
        {
            "path": "globalACI",
            "op": "add",
            "value": "(targetattr=\"uid||entryUUID||isMemberOf\")(version 3.0; acl \"pingdatagovernance access to selected attributes\"; allow(all) userdn=\"ldap:///cn=pingdatagovernance,cn=Root DNs,cn=config\";)"
        },
        {
            "path": "globalACI",
            "op": "add",
            "value": "(target=\"ldap:///{{userBaseDN}}\")(targetattr=\"*||+\")(version 3.0; acl \"pingdatagovernance access to user store data\"; allow(all) userdn=\"ldap:///cn=pingdatagovernance,cn=Root DNs,cn=config\";)"
        }
    ]
}

{
    "modifications": [
        {
            "attributeName": "aci",
            "modificationType": "set",
            "values": [
                "(targetattr=\"userPassword\")(version 3.0; acl \"Allow users to update their own password\"; allow (write) userdn=\"ldap:///self\";)",
                "(version 3.0; acl \"PingFed Proxy Access\"; allow(proxy) userdn=\"ldap:///cn=pingfederate,cn=root dns,cn=config\";)",
                "(targetattr!=\"userPassword\")(version 3.0; acl \"Allow the pingfederate user to do everything in this branch\"; allow(all) userdn=\"ldap:///cn=pingfederate,cn=root dns,cn=config\";)",
                "(targetattr=\"userPassword\")(version 3.0; acl \"Allow the pingfederate user to update passwords\"; allow (write) userdn=\"ldap:///cn=pingfederate,cn=root dns,cn=config\";)",
                "(targetattr=\"isMemberOf\")(version 3.0; acl \"Allow the pingfederate user to get memberships\"; allow (read) userdn=\"ldap:///cn=pingfederate,cn=root dns,cn=config\";)",
                "(targetcontrol=\"1.3.6.1.4.1.42.2.27.9.5.8\")(version 3.0; acl \"Access to the Account Usability Control\"; allow (read) userdn=\"ldap:///cn=pingfederate,cn=root dns,cn=config\";)",
                "(targetcontrol=\"1.3.6.1.4.1.30221.2.5.40\")(version 3.0; acl \"Access to the Password Validation Details Request Control\"; allow (read) userdn=\"ldap:///{{userBaseDN}}\";)"
            ]
        }
    ]
}

